import React from 'react';
import { ScrollMenu, VisibilityContext } from 'react-horizontal-scrolling-menu';
import 'react-horizontal-scrolling-menu/dist/styles.css';

const getItems = () =>
  Array(20)
    .fill(0)
    .map((_, ind) => ({ id: `element-${ind}` }));

function Main2() {
  const [items, setItems] = React.useState(getItems);
  const [selected, setSelected] = React.useState([]);

  const isItemSelected = (id) => !!selected.find((el) => el === id);

  const handleClick = (id, visibility) => {
    const itemSelected = isItemSelected(id);

    setSelected((currentSelected) =>
      itemSelected
        ? currentSelected.filter((el) => el !== id)
        : currentSelected.concat(id),
    );

    // Scroll to the clicked item
    visibility.scrollToItemId(id);
  };

  return (
    <VisibilityContext.Consumer> {/* Use VisibilityContext.Consumer */}
      {visibility => (
        <ScrollMenu LeftArrow={props => <LeftArrow {...props} visibility={visibility} />} RightArrow={props => <RightArrow {...props} visibility={visibility} />}>
          {items.map(({ id }) => (
            <Card
              itemId={id} // NOTE: itemId is required for tracking items
              title={id}
              key={id}
              selected={isItemSelected(id)}
              onClick={() => handleClick(id, visibility)}
            />
          ))}
        </ScrollMenu>
      )}
    </VisibilityContext.Consumer>
  );
}

const LeftArrow = ({ visibility }) => { // Receive visibility as prop
  const { isFirstItemVisible, scrollPrev } = visibility; // Destructure visibility
  return (
    <button disabled={isFirstItemVisible} onClick={scrollPrev} className="left"> {/* Change Arrow to button */}
      Left
    </button>
  );
};

const RightArrow = ({ visibility }) => { // Receive visibility as prop
  const { isLastItemVisible, scrollNext } = visibility; // Destructure visibility
  return (
    <button disabled={isLastItemVisible} onClick={scrollNext} className="right"> {/* Change Arrow to button */}
      Right
    </button>
  );
};

function Card({ onClick, selected, title, itemId }) {
  return (
    <div
      onClick={onClick}
      style={{
        width: '160px',
      }}
      tabIndex={0}
    >
      <div className={`card ${selected ? 'selected' : ''}`}>
        <div>{title}</div>
      </div>
    </div>
  );
}

export default Main2;
