// Navbar.js
import React, { Component } from 'react';
import Navitems from './Navitems';
import HamburgerMenu from './HamburgerMenu'; // Import the HamburgerMenu component

class Navbar extends Component {
    constructor(props) {
        super(props);
        this.state = {
            'NavItemActive': '',
            'menuOpen': false // Add state for menu open/close
        }
    }

    // Function to toggle menu open/close
    toggleMenu = () => {
        this.setState(prevState => ({
            menuOpen: !prevState.menuOpen
        }));
    }

    activeitem = (item) => {
        if (this.state.NavItemActive.length > 0) {
            document.getElementById(this.state.NavItemActive).classList.remove('active');
        }
        this.setState({ 'NavItemActive': item }, () => {
            document.getElementById(this.state.NavItemActive).classList.add('active');
        });
    }

    render() {
        return (
            <nav>
                {/* Include the HamburgerMenu component */}
                <HamburgerMenu toggleMenu={this.toggleMenu} />
                <ul className={this.state.menuOpen ? 'menu-open' : ''}>
                    <Navitems item="White Paper" tolink="/" activenav={this.activeitem}></Navitems>
                    <Navitems item="Announcement" tolink="/Announcement" activenav={this.activeitem}></Navitems>
                    <Navitems item="Gallery" tolink="/Gallery" activenav={this.activeitem}></Navitems>
                    <Navitems item="Store" tolink="/Store" activenav={this.activeitem}></Navitems>
                    <Navitems item="About Us" tolink="/About Us" activenav={this.activeitem}></Navitems>
                    <Navitems item="Blockchain" tolink="/Blockchain" activenav={this.activeitem}></Navitems>
                    <Navitems item="Sign in" tolink="/Sign in" activenav={this.activeitem}></Navitems>
                </ul>
            </nav>
        );
    }
}

export default Navbar;
